export const setSettings = settings => {
  return {
    type: 'SET_SETTINGS',
    payload: settings
  };
}

const changeSettings = (prevSettings, newSettings = {}) => {
  dispatch(settingsActions.setSettings({
    ...prevSettings,
    page: 1,
    ...newSettings
  }));
};
