import React from 'react';
import { Link } from 'react-router-dom';
import ImageWithLoadingContainer from '../containers/ImageWithLoadingContainer';

function ListItem({ handleClick, movie, routePath }) {
  return (
    <div className="ListItem">
      <img 
        className="link image-content" 
        onClick={handleClick} 
        src={`https://image.tmdb.org/t/p/original${movie.poster_path}`} 
        alt={movie.title}
      />
      <ImageWithLoadingContainer
        className="link image-content" 
        onClick={handleClick} 
        src={`https://image.tmdb.org/t/p/original${movie.poster_path}`} 
        alt={movie.title}
      />
      <div className="info">
        <Link to={routePath} className="title link">
          {movie.title}
        </Link>
        <p className="overview">{movie.overview}</p>
        <Link className="view-more link" to={routePath}>More Info</Link>
      </div>
    </div>
  );
}

export default ListItem;
