import Catalog from '../components/Catalog';
import appDataActions from '../actions/appDataActions';
import { useDispatch, useSelector } from 'react-redux';

export default function CatalogContainer({ history }) {
  const data = useSelector(state => state.data);

  return (
    <Catalog />
  )
}

const mapStateToProps = (state, ownProps) => {
  return {
    data: state.appData.data,
    dataById: state.appData.dataById,
    totalPages: state.appData.totalPages
  }
}

const mapDispatchToProps = (dispatch, ownProps) => {
  return {
    setData(data) {
      dispatch(appDataActions.setData(data))
    },
    setDataById(data) {
      dispatch(appDataActions.setDataById(data))
    },
    setTotalPages(data) {
      dispatch(appDataActions.setTotalPages(data))
    },
    redirect(id) {
      ownProps.history.push(`catalog/${id}`);
    }
  }
}
