import React from 'react';
import API from '../API'

class Catalog extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      title
    }
    const id = this.props.match.params.id;
    
    (async () => {
      if (!this.props.items[id]) {
        const resp = await API.getDetails(id);
        await this.props.addMovie(resp);
      }
      this.setState({title: this.props.items[id].title});
    })();
    
  }

  render() {
    return (
      <div>
        {this.state.title}
      </div>
    );
  }
}

export default Catalog;
