import React from 'react';
import API from '../API'
import TextField from '@material-ui/core/TextField';
import {
  BrowserRouter as Router,
  Route,
  Switch
} from 'react-router-dom';

class Catalog extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      title: '',
      id: this.props.match.params.id
    }
    
    (async () => {
      if (!this.props.items[this.state.id]) {
        const resp = await API.getDetails(this.state.id);
        await this.props.addMovie(resp);
      }
      console.log(this.props.items);
      this.setState({title: this.props.items[this.state.id].title});
    })();
    
  }

  onEnter(e) {
    if (e.keyCode === 13) { 
      const newId = e.target.value;
      this.props.history.push(`/catalog/${newId}`);
      this.setState({id: newId})
    }
  }

  render() {
    console.log(this.props.match.params.id);
    return (
      <div>
        {this.state.title}
        <TextField label="id" onKeyUp={onEnter} />
      </div>
    );
  }
}

export default Catalog;
