import React from 'react';
import { Droppable, Draggable } from 'react-beautiful-dnd';

export default function Column({ column, tasks }) {
  return (
    <div style={{
      margin: `8px`,
      border: `1px solid lightgrey`,
      borderRadius: `2px`
    }}>
      <h3 style={{
        padding: `8px`
      }}>
        {column.title}
      </h3>
      <Droppable droppableId={column.id}>
        {(provided, snapshot) => (
          <div 
            style={{
              padding: `8px`
            }}
            ref={provided.innerRef}
            {...provided.droppableProps}
          >
            {/* {tasks.map((task, index) => (
              <Draggable
                index={index}
                draggableId={task.id}
              >
                {provided => (
                  <div 
                    ref={provided.innerRef}
                    {...provided.draggableProps}
                    {...provided.dragHandleProps}
                    style={{
                      border: `1px solid lightgrey`,
                      borderRadius: `2px`,
                      padding: `8px`,
                      marginBottom: `8px`
                    }}
                  >
                    {task.content}
                  </div>
                )}
              </Draggable>
            ))} */}
            <Draggable
              index={0}
              draggableId="d1"
            >
              {(provided, snapshot) => (
                <div 
                  ref={provided.innerRef}
                  {...provided.draggableProps}
                  {...provided.dragHandleProps}
                >
                 <h4 style={{
                    border: `1px solid lightgrey`,
                    borderRadius: `2px`,
                    padding: `8px`,
                    marginBottom: `8px`
                  }}>My draggable 2</h4>
                </div>
              )}
            </Draggable>
            <Draggable
              index={1}
              draggableId="d2"
            >
              {(provided, snapshot) => (
                <div 
                  ref={provided.innerRef}
                  {...provided.draggableProps}
                  {...provided.dragHandleProps}
                >
                 <h4 style={{
                    border: `1px solid lightgrey`,
                    borderRadius: `2px`,
                    padding: `8px`,
                    marginBottom: `8px`
                  }}>My draggable</h4>
                </div>
              )}
            </Draggable>
            {provided.placeholder}
          </div>
        )}
      </Droppable>
    </div>
  );
}
