import React, { useState, useCallback } from 'react';
import Filters from '../components/Filters';
import debounce from 'lodash/debounce';
import { useSelector, useDispatch } from 'react-redux';
import actions from '../actions/reducerActions';

function FiltersContainer() {
  const settings = useSelector(state => state.settings);

  const dispatch = useDispatch();

  const changeSettings = (prevSettings, newSettings = {}) => {
    dispatch(actions.setSettings({
      ...prevSettings,
      page: 1,
      ...newSettings
    }));
  }

  const changeSettingsDebounced = useCallback(debounce(changeSettings, 400), []);

  const onSearchQueryChange = e => {
    setSearchQuery(e.target.value);
    changeSettingsDebounced({ searchQuery: e.target.value });
  }

  const onSortingChange = e => {
    setSorting(e.target.value);
    changeSettings({ sorting: e.target.value });
  }

  return (
    <Filters 
      onSortingChange={onSearchQueryChange}
      onSearchQueryChange={changeSettingsDebounced}
      searchQuery={settings.searchQuery}
      sorting={settings.sorting}
    />
  );
}

export default FiltersContainer;
