import React from 'react';
import './styles/App.css';
import Catalog from './Catalog';
import CatalogItem from './CatalogItem';
import {
  BrowserRouter as Router,
  Route,
  Switch
} from 'react-router-dom'

// const PAGE_SIZE = 5;

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      currentPageIds: [],
      itemsById: {},
      page: localStorage.getItem('page') | 1,
      sorting: 'popular'
    }

    if (localStorage.getItem('sorting')) {
      this.state.setState({sorting: localStorage.getItem('sorting')});
    }

    console.log(this.state.sorting);

    this.addData = this.addData.bind(this);
    this.setSorting = this.setSorting.bind(this);
  }

  addData(data) {
    this.setState({...data});
  }

  setSorting(sorting = 'popular') {
    this.state.setState({sorting: sorting});
    localStorage.setItem('sorting', sorting);
    console.log(this.state.sorting);
  }

  render() {
    return (
      <Router>
        <Switch>
            <Route path='/catalog'
              render={routerProps => <Catalog callback={this.setSorting} appAddData={this.addData} {...routerProps} itemsIds={this.state.currentPageIds} items={this.state.itemsById} />}
            />
            {/* <Route path='/catalog/:id' component={CatalogItem}/> */}
        </Switch>
      </Router>
    );
  }
}

export default App;
