export const getMovieByQuery = settings => async dispatch => {
  dispatch(appDataActions.setFetching({
    isFetching: true,
    isFetched: false
  }));

  try {
    const { results, total_pages } = await API.getMovieByQuery(settings);
    dispatch(appDataActions.setDataById({
      ...additionalDataById(results)
    }));
    dispatch(appDataActions.setData(results));
    dispatch(appDataActions.setTotalPages(total_pages));
    dispatch(appDataActions.setFetching({
      isFetching: false,
      isFetched: true
    }));
  } catch (error) {
    dispatch(appDataActions.setError(error));
    dispatch(appDataActions.setFetching({
      isFetching: false,
      isFetched: false
    }));
  }
};

export const discoverMovies = settings => async dispatch => {
  dispatch(appDataActions.setFetching(true));

  try {
    const response = await API.getMovieDiscover(settings);
    dispatch(appDataActions.setData(response));
    dispatch(appDataActions.setFetching(false));
    dispatch(appDataActions.setFetched(true));
  } catch (error) {
    dispatch(appDataActions.setError(error));
    dispatch(appDataActions.setFetching(false));
    dispatch(appDataActions.setFetched(false));
  }
}
