import React from 'react';
import API from '../API'
import TextField from '@material-ui/core/TextField';
import {
  BrowserRouter as Router,
  Route,
  Switch
} from 'react-router-dom';

class Catalog extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      title: ''
    }
    const id = this.props.match.params.id;
    
    (async () => {
      if (!this.props.items[id]) {
        const resp = await API.getDetails(id);
        await this.props.addMovie(resp);
      }
      console.log(this.props.items);
      this.setState({title: this.props.items[id].title});
    })();
    
  }

  onEnter(e) {
    if (e.keyCode === 13) {
    }
  }

  render() {
    console.log(this.props.routerProps);
    return (
      <div>
        {this.state.title}
        <TextField
          label="id"
          onKeyUp={(e) => { if (e.keyCode === 13) { this.props.routerProps.history.push(`/catalog/${e.target.value}`) } }}
            />
      </div>
    );
  }
}

export default Catalog;
