import React from 'react';
import { connect } from 'react-redux';

function App() {
  return (
    <div className="App">
      <input type='text' />
      <button>reset</button>
    </div>
  )
}

const mapStateToProps = state => {
  return {
    text: state.text
  };
};

const mapDispatchToProps = dispatch => {
  return {
    changeText(text) {
      dispatch({
        type: "CHANGE",
        text
      })
    },
    resetText() {
      dispatch({
        type: "RESET"
      })
    }
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(App);
