import React from 'react';
import API from '../API'
import TextField from '@material-ui/core/TextField';

class Catalog extends React.Component {
  constructor(props) {
    super(props);
    
    if (!this.props.items[this.props.match.params.id]) {
      API.getDetails(this.props.match.params.id)
      .then(resp => {
        this.props.addMovie(resp);
      });
    }    
  }
  render() {
    return (
      <div>
        {/* {this.props.items[this.props.match.params.id].title} */}
      </div>
    );
  }
}

export default Catalog;
