import React from 'react';
import './styles/ListItem.css';
import { Link, Redirect } from 'react-router-dom'

class ListItem extends React.Component {
  constructor(props) {
    super(props);

    this.redirect = this.redirect.bind(this);
  }

  redirect() {
    this.props.history.push(`catalog/${this.props.movie.id}`);
  }

  render() {
    return (
      <div className="ListItem">
        <div className="image-content">
          <img onClick={this.redirect} src={`https://image.tmdb.org/t/p/original${this.props.movie.poster_path}`} alt={this.props.movie.title}/>
        </div>
        <div className="info">
          <div className="title-date">
            <div onClick={this.redirect} className="title">
              {this.props.movie.title}
            </div>
            <span className="date">{this.props.movie.release_date}</span>
          </div>
          <p className="overview">{this.props.movie.overview}</p>
          <p className="view-more">More Info</p>
        </div>
      </div>
    );
  }
}

export default ListItem;
