import React from 'react';
import './styles/App.css';
import Catalog from './Catalog';
// import CatalogItem from './CatalogItem';
import {
  BrowserRouter as Router,
  Route,
  Switch
} from 'react-router-dom'

// const PAGE_SIZE = 5;

function initSorting() {
  const storageSorting = localStorage.getItem('sorting');
  if (['popular', 'top', 'latest'].includes(storageSorting)) {
    return storageSorting;
  }

  localStorage.setItem('sorting', 'popular');
  return localStorage.getItem('sorting');;
}

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      currentPageIds: [],
      itemsById: {},
      page: localStorage.getItem('page') | 1,
      sorting: initSorting()
    }

    this.addData = this.addData.bind(this);
    this.setSorting = this.setSorting.bind(this);
  }

  addData(data) {
    this.setState({...data});
  }

  setSorting(sorting = 'popular') {
    this.setState({sorting}, () => {
      localStorage.setItem('sorting', sorting);
      console.log(this.state.sorting);
    });
  }

  setpage(page = 1) {
    this.setState({page}, () => {
      localStorage.setItem('page', page);
      console.log(this.state.page);
    });
  }

  render() {
    return (
      <Router>
        <Switch>
            <Route path='/catalog'
              render={routerProps => <Catalog callback={this.setSorting} appAddData={this.addData} {...routerProps} itemsIds={this.state.currentPageIds} items={this.state.itemsById} sorting={this.state.sorting} />}
            />
            {/* <Route path='/catalog/:id' component={CatalogItem}/> */}
        </Switch>
      </Router>
    );
  }
}

export default App;
