import React from 'react';
import './styles/App.css';
import Catalog from './Catalog';
import CatalogItem from './CatalogItem';
import {
  BrowserRouter as Router,
  Route,
  Switch
} from 'react-router-dom';

function initializeSorting() {
  if (!['popular', 'top', 'trending'].includes( localStorage.getItem('sorting') )) {
    localStorage.setItem('sorting', 'trending');
  }
  return localStorage.getItem('sorting');
}

function initializePage() {
  if (typeof localStorage.getItem('sorting') !== Number) {
    localStorage.setItem('currentPage', 1);
  }
  return localStorage.getItem('currentPage');
}

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      currentPageIds: [],
      itemsById: {4: 'asd', '3': 'dsa'},
      currentPage: initializePage(),
      sorting: initializeSorting()
    }

    this.setSorting = this.setSorting.bind(this);
    this.setCurrentPage = this.setCurrentPage.bind(this);
    this.addMovie = this.addMovie.bind(this);
  }

  setSorting(sorting) {
    this.setState({sorting}, () => {
      localStorage.setItem('sorting', this.state.sorting);
      this.setCurrentPage(1);
    });
  }

  setCurrentPage(page) {
    this.setState({page}, () => {
      localStorage.setItem('page', this.state.page);
    });
  }

  addMovie(movie) {
    this.setState({itemsById: Object.assign({}, this.state.itemsById, { [movie.id]: movie })});
  }

  render() {
    return (
      <Router>
        <Switch>
            <Route exact path='/catalog'
              render={routerProps => <Catalog {...routerProps} itemsIds={this.state.currentPageIds} items={this.state.itemsById} page={this.state.page} sorting={this.state.sorting} />}
            />
            <Route path='/catalog/:id'
              render={routerProps => <CatalogItem {...routerProps} addMovie={this.addMovie} items={this.state.itemsById} />}
            />
        </Switch>
      </Router>
    );
  }
}

export default App;
