import axios from 'axios';

const api_key = '4f7b4e11c29b41270f152fa71698f126';

async function callApi(path, params) {
   const response = await axios.get(path, {...params , api_key});
}

export default {
  async getDetails(id) {
    return axios.get(`https://api.themoviedb.org/3/movie/${id}`, {params: { api_key }});
  },
  async getPopular(page = 1) {
    return axios.get(`https://api.themoviedb.org/3/movie/popular`, {params: { api_key, page }});
  },
  async getTopRated(page = 1) {
    return axios.get(`https://api.themoviedb.org/3/movie/top_rated`, {params: { api_key, page }});
  },
  async getDetails() {
    return axios.get(`https://api.themoviedb.org/3/trending/movie/week`, {params: { api_key }});
  },
}
