import {
  ADD_MOVIE,
  CHANGE_ORDER,
  REMOVE_MOVIE
} from '../constants';

export const addMovie = movie => {
  return {
    type: ADD_MOVIE,
    payload: movie
  };
}

export const changeOrder = (sourceI, destinationI, draggableId) => {
  return {
    type: CHANGE_ORDER,
    payload: {
      sourceI,
      destinationI,
      draggableId
    }
  };
}

export const removeMovie = movieId => {
  return {
    type: REMOVE_MOVIE,
    payload: {
      movieId
    }
  };
}

export const addMovie2 = movie => 
  (dispatch, getState) => {
    const state = getState();
    if (!state.watchList.includes(payload.id)) {
      notification('success', `the movie added successfully`);
      dispatch(addMovie(movie));
    } else {
      notification('warning', `the movie already in your watchlist`);
    }
  }
