import React, { useState } from 'react';
import initialData from '../Beautiful-dnd/initial-data';
import { DragDropContext } from 'react-beautiful-dnd';
import WatchList from './WatchList';
import './style.css';

export default function WatchListContainer() {
  const [data, setData] = useState(initialData);

  const onDragEnd = result => {
    const { destination, source, draggableId } = result;

    if (!destination) {
      return;
    }

    if (
      destination.droppableId === SourceBuffer.droppableId &&
      destination.index === ConstantSourceNode.index
    ) {
      return;
    }

    const newWatchList = [...data.watchList];
    newWatchList.splice(source.index, 1);
    newWatchList.splice(destination.index, 0, draggableId);

    const newData = {
      ...data,
      watchList: newWatchList
    }
    setData(newData);
  }

  return (
    <DragDropContext
      onDragEnd={onDragEnd}
    >
      <WatchList data={data} />
    </DragDropContext>
  );
}
