import axios from 'axios';

const api_key = '4f7b4e11c29b41270f152fa71698f126';

async function callApi(url, page, query) {
  const response = await axios.get(url, { params: { api_key, page, query } });
  return response.data;
}

export default {
  async getDetails(id) {
    return callApi(`https://api.themoviedb.aorg/3/movie/${id}`);
  },
  async getPopular(page) {
    return callApi(`https://api.themoviedb.org/3/movie/popular`, page);
  },
  async getTopRated(page) {
    return callApi(`https://api.themoviedb.org/3/movie/top_rated`, page)
  },
  async getTrending(page) {
    return callApi(`https://api.themoviedb.org/3/trending/movie/week`, page);
  },
  async getMovieByQuery(page, query) {
    return callApi(`https://api.themoviedb.org/3/search/movie`, page, query);
  }
}
