import React from 'react';
import API from '../API'
import './styles/Catalog.css';
import TextField from '@material-ui/core/TextField';
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';
import ListItem from './ListItem';

class Catalog extends React.Component {
  constructor(props) {
    super(props);

    this.sorting = localStorage.getItem('sorting') ? localStorage.getItem('sorting') : 'trending';

    this.state = {
      pageIds : []
    }

    this.movie = {
      "popularity": 197.549,
      "vote_count": 8373,
      "video": false,
      "poster_path": "/udDclJoHjfjb8Ekgsd4FDteOkCU.jpg",
      "id": 475557,
      "adult": false,
      "backdrop_path": "/n6bUvigpRFqSwmPp1m2YADdbRBc.jpg",
      "original_language": "en",
      "original_title": "Joker",
      "genre_ids": [
        80,
        18,
        53
      ],
      "title": "Joker",
      "vote_average": 8.3,
      "overview": "During the 1980s, a failed stand-up comedian is driven insane and turns to a life of crime and chaos in Gotham City while becoming an infamous psychopathic crime figure.",
      "release_date": "2019-10-02"
    };

    this.onEnter = this.onEnter.bind(this);
  }

  onEnter(e) {
    if (e.keyCode === 13) {
      API.getTrending(e.target.value)
      .catch(console.log)
      .then(console.log)
    }
  }

  onSelectChange = (e) => {
    localStorage.setItem('sorting', e.target.value);
  }

  render() {
    return (
      <div className="Catalog">
        <div className="filters">
          <TextField label="Search" onKeyUp={this.onEnter} />
          <Select onChange={this.onSelectChange} defaultValue={this.sorting} >
            <MenuItem value="trending">trending</MenuItem>
            <MenuItem value="popular">popular</MenuItem>
            <MenuItem value="top">top</MenuItem>
          </Select>
        </div>
        <div className="list">
          <ListItem history={this.props.history} movie={this.movie}/>
        </div>
      </div>
    );
  }
}

export default Catalog;
