import React from 'react';
import API from '../API'
import './styles/Catalog.css';
import TextField from '@material-ui/core/TextField';
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';
import ListItem from './ListItem';

class Catalog extends React.Component {
  constructor(props) {
    super(props);

    this.sorting = localStorage.getItem('sorting') ? localStorage.getItem('sorting') : 'trending';

    this.state = {
      pageIds : []
    }

    

    this.onEnter = this.onEnter.bind(this);
  }

  onEnter(e) {
    if (e.keyCode === 13) {
      API.getTrending(e.target.value)
      .catch(console.log)
      .then(console.log)
    }
  }

  onSelectChange = (e) => {
    localStorage.setItem('sorting', e.target.value);
  }

  render() {
    return (
      <div className="Catalog">
        <div className="filters">
          <TextField label="Search" onKeyUp={this.onEnter} />
          <Select onChange={this.onSelectChange} defaultValue={this.sorting} >
            <MenuItem value="trending">trending</MenuItem>
            <MenuItem value="popular">popular</MenuItem>
            <MenuItem value="top">top</MenuItem>
          </Select>
        </div>
        <div className="list">
          <ListItem history={this.props.history} movie={this.movie}/>
        </div>
      </div>
    );
  }
}

export default Catalog;
