import axios from 'axios';

const api_key = '4f7b4e11c29b41270f152fa71698f126';

async function callApi(path, params = {}) {
   try {
    const response = await axios.get(path, { params: { ...params , api_key } });
    if (!response.ok) {
      throw new Error(`${response.status}: ${response.statusText}`);
    }
   } catch(error) {
     throw error;
   }
}

export default {
  async getDetails(id) {
    try {
      return await callApi(`https://api.themoviedb.org/3/movie/${id}`);
    } catch(error) {
      throw error;
    }
  },
  async getPopular(page = 1) {
    try {
      return await callApi(`https://api.themoviedb.org/3/movie/popular`, { page });
    } catch(error) {
      throw error;
    }
  },
  async getTopRated(page = 1) {
    try {
      return await callApi(`https://api.themoviedb.org/3/movie/top_rated`, { page });
    } catch(error) {
      throw error;
    }
  },
  async getTrending(page = 1) {
    try {
      return await callApi(`https://api.themoviedb.org/3/trending/movie/week`, { page });
    } catch(error) {
      throw error;
    }
  },
}
