import {
  ADD_MOVIE,
  CHANGE_ORDER
} from '../constants';

export const addMovie = movie => {
  return {
    type: ADD_MOVIE,
    payload: movie
  };
}

export const changeOrder = (sourceI, destinationI, draggableId) => {
  return {
    type: CHANGE_ORDER,
    payload: {
      sourceI,
      destinationI,
      draggableId
    }
  };
}
