import React from 'react';
import './styles/App.css';
import Catalog from './Catalog';
import CatalogItem from './CatalogItem';
import {
  BrowserRouter as Router,
  Route,
  Switch
} from 'react-router-dom'

// const PAGE_SIZE = 5;

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      currentPageIds: [],
      itemsById: {},
      page: localStorage.getItem('page') | 1,
      sorting: localStorage.getItem('sorting') | 'popular'
    }

    this.addData = this.addData.bind(this);
  }

  addData(data) {
    this.setState({...data});
  }

  render() {
    return (
      <Router>
        <Switch>
            <Route path='/catalog'
              render={routerProps => <Catalog appAddData={this.addData} {...routerProps} itemsIds={this.state.currentPageIds} items={this.state.itemsById} />}
            />
            <Route path='/catalog/:id' component={CatalogItem}/>
        </Switch>
      </Router>
    );
  }
}

export default App;
