import React from 'react';
import API from '../API';

class Catalog extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      movie: {}
    }

    API.getDetails(this.props.match.params.id)
    .then(movie => {
      this.setState({ movie })
    })
  }

  render() {
    return (
      <div>
        {this.props.match.params.id}
      </div>
    );
  }
}

export default Catalog;
