import API from '../API';
import appDataActions from '../actions/appDataActions';

export default {
  getMovieById: id => dispatch => {
    dispatch( setFetching(true) );
  
    return API.getDetails(id)
      .then(response => {
        dispatch( setData(response) )
        dispatch( setFetching(false) );
        dispatch( setFetched(true) );
      })
      .catch(error => {
        dispatch( setError(error) );
        dispatch( setFetching(false) );
        dispatch( setFetched(false) );
      })
  },

  getMovieByQuery: settings => dispatch => {
    dispatch( setFetching(true) );
  
    return API.getMovieByQuery(settings)
      .then(({ results, total_pages}) => {
        const additionalDataById = results.reduce((res, curr) => {
          res[curr.id] = curr;
          return res;
        }, {});
        dispatch(appDataActions.setDataById({
          ...additionalDataById
        }));
        dispatch( appDataActions.setData(results) );
        dispatch( appDataActions.setTotalPages(total_pages) );
        dispatch( appDataActions.setFetching({ isFetching: false, isFetched: true }) );
      })
      .catch(error => {
        dispatch( setError(error) );
        dispatch( setFetching(false) );
        dispatch( setFetched(false) );
      })
  },
  
  discoverMovies: settings => dispatch => {
    dispatch( setFetching(true) );
  
    return API.getMovieDiscover(settings)
      .then(response => {
        dispatch( setData(response) )
        dispatch( setFetching(false) );
        dispatch( setFetched(true) );
      })
      .catch(error => {
        dispatch( setError(error) );
        dispatch( setFetching(false) );
        dispatch( setFetched(false) );
      })
  }
}
