import React from 'react';
import API from '../API'
import TextField from '@material-ui/core/TextField';

class Catalog extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      title: ''
    }
    const id = this.props.match.params.id;
    
    (async () => {
      if (!this.props.items[id]) {
        const resp = await API.getDetails(id);
        await this.props.addMovie(resp);
      }
      console.log(this.props.items);
      this.setState({title: this.props.items[id].title});
    })();
    
  }

  onEnter(e) {
    if (e.keyCode === 13) {
      API.getTrending(e.target.value)
      .catch(console.log)
      .then(console.log)
    }
  }

  render() {
    return (
      <div>
        {this.state.title}
        <TextField label="id" onKeyUp={this.onEnter} />
      </div>
    );
  }
}

export default Catalog;
